mapper.update:
UPDATE wx_user SET openId =
#{openId} , bindTime = now()
WHERE name = #{name} and phone=#{phone};

mapper.select:
SELECT id, subscribe, nickName nickname , openId openid, sex, language, city, province, country, headImgUrl headimgurl, subscribeTime subscribe_time, unionId unionid, remark, groupId groupid, infoGetTime FROM wx_plateform_user_info WHERE 1 = 1
AND openId = #{openId}
LIMIT start, limit;
